    def UI( self, *args ):
        '''
        Create UI
        '''
        # - Get shape
        shape_list= self.shape_dict.keys()
        shape_list.append( 'none' )

        # --- Define the UI window Name
        sWindow = 'Shape_Manager_Window'

        # --- Test the UI existance
        if mc.window( sWindow, ex=True, q=True, w=180 ):
        	mc.deleteUI( sWindow )

        # - Create window
        mc.window( sWindow, title="Shape Manager" )

        # Create column Layout
        mc.columnLayout( 'ui_column', adjustableColumn=True, w=180 )
        
        mc.optionMenu( 'ui_shape', label=' Select Shape' )
        for shape_name in shape_list:
            mc.menuItem( label = shape_name )

        mc.floatSliderGrp( 'ui_size', label=' Size', field= True, minValue=0, maxValue=10, fieldMinValue=0, fieldMaxValue=100.0, value= 1, columnAlign3= ['left', 'right', 'right'], columnWidth3= [65, 40, 30] )

        mc.separator( style='none', height=10 )
        mc.radioButtonGrp( 'ui_orientation', label=' Orientation', labelArray3=['X', 'Y', 'Z'], select= 0, numberOfRadioButtons=3, columnWidth4= [65, 25, 25, 25 ], columnAlign4= ['left', 'right', 'right', 'right'] )

        mc.separator( style='none', height=10 )
        mc.rowLayout(numberOfColumns= 4, columnAlign4=('left', 'right', 'right', 'right'), columnWidth4= [65, 27, 27, 27] )
        mc.text( ' Mirror' )
        mc.checkBox( 'ui_mirror_x', label='X', v=False, annotation='' )
        mc.checkBox( 'ui_mirror_y', label='Y', v=False, annotation='' )
        mc.checkBox( 'ui_mirror_z', label='Z', v=False, annotation='' )
        
        mc.setParent( 'ui_column' )
        
        mc.separator( style='none', height=15 )
        mc.checkBox( 'ui_remove_previous_shape', label='Remove previous Shapes', v=True, annotation='' )

        mc.separator( style='none', height=5 )
        # Create a button
        mc.button( label='Add selected Shape', command= self.RUN, h=40, w=180 )

        # Show window
        mc.showWindow( sWindow )

    def RUN( self, *args ):
        '''
        '''
        ui_shape= mc.optionMenu( 'ui_shape', q=True, v=True )
        ui_size= mc.floatSliderGrp('ui_size', q=True, v=True )
        ui_remove_previous_shape= mc.checkBox( 'ui_remove_previous_shape', q=True, v=True )

        ui_orientation= mc.radioButtonGrp( 'ui_orientation', q=True, select= True )
        
        ui_mirror_x= mc.checkBox( 'ui_mirror_x', q=True, v=True )
        ui_mirror_y= mc.checkBox( 'ui_mirror_y', q=True, v=True )
        ui_mirror_z= mc.checkBox( 'ui_mirror_z', q=True, v=True )
        
        orient = ['X', 'Y', 'Z']
        
        mirror_list= [1, 1, 1]
        if ui_mirror_x:
            mirror_list[0] = -1
        if ui_mirror_y:
            mirror_list[1] = -1
        if ui_mirror_z:
            mirror_list[2] = -1
        
        self.add_Shape( shape= ui_shape, size= ui_size, orient= orient[ ui_orientation -1 ], mirror = mirror_list, removePrevious= ui_remove_previous_shape )

    def add_Shape( self, shape= 'circle', size= 1, orient= 'X', mirror= [1,1,1], removePrevious= True):
        '''
        '''
        if not shape == 'none':
            degree= self.shape_dict[shape]['degree']
            point= self.shape_dict[shape]['point']
            knot= self.shape_dict[shape]['knot']
        
        # --- Get Guide element
        selection= mc.ls( sl= True )
        
        if not selection:
            selection = [mc.group( em=True, name= shape + '_ctrl' )]

        for item in selection:
            # - Check item is a Transform
            if mc.nodeType( item ) in ['transform', 'joint']:
                current_shape = mc.listRelatives(item, s=True)
                if current_shape:
                    if not mc.nodeType( current_shape[0] ) == 'nurbsCurve':
                        continue
            else:
                continue
            
            # - Remove previous Shape if needs
            if removePrevious:
                previous_shape = mc.listRelatives(item, s=True)
                if previous_shape:                
                    mc.delete( previous_shape )
            
            if not shape == 'none':
                # - Create new shape
                curve_shape = mc.curve( d= degree, p=  point, k= knot )
                shape_obj= mc.listRelatives(curve_shape, s=True)[0]
                mc.parent( shape_obj, item, s=True, r=True )
                shape_obj= mc.rename( shape_obj, item + 'Shape')
                mc.delete( curve_shape )
                
                # ---  edit curve
                if orient == 'Y' :
                    mc.rotate(0, 0, 90, shape_obj+ '.cv[*]', r=True)
                if orient == 'Z' :
                    mc.rotate(0, -90, 0, shape_obj+ '.cv[*]', r=True)
                    
                # - Mirror
                mc.scale( mirror[0], mirror[1], mirror[2], shape_obj+ '.cv[*]', r=True)
                # - Size
                mc.scale( size, size, size, shape_obj+ '.cv[*]', r=True)

        # --- reSelect previous Selection
        mc.select( selection )

